import React, { useState, useEffect } from 'react';
import { fetchStockData } from './api/stockApi';
import Controls from './components/Controls/Controls';
import Visualization from './components/Visualizations/Visualization';
import './styles/main.css';
import PerformanceMetrics from './components/Metrics/PerformanceMetrics';
import DataSourceToggle from './components/Controls/DataSourceToggle';
import StockInfoPanel from './components/Metrics/StockInfoPanel';

function App() {
  const [stocks, setStocks] = useState([]);
  const [useLiveData, setUseLiveData] = useState(false);
  const [selectedStock, setSelectedStock] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSorting, setIsSorting] = useState(false);
  const [activeAlgorithm, setActiveAlgorithm] = useState('bubble');
  const [sortKey, setSortKey] = useState('price');
  const [speed, setSpeed] = useState(500);
  const [visualizationType, setVisualizationType] = useState('bar');
  const [metrics, setMetrics] = useState({
  comparisons: 0,
  swaps: 0,
  startTime: null,
  timeTaken: 0
});


  // Initial data load
  useEffect(() => {
    handleDataChange(useLiveData);
  }, []);

  // Example stock selection handler
  const handleStockSelect = (stock) => {
    setSelectedStock(stock);
  };


const handleDataChange = async (useLive) => {
    setIsLoading(true);
    try {
      const data = useLive ? await fetchStockData() : fetchMockData();
      setStocks(data);
      setSelectedStock(null); // Reset selection when data changes
    } catch (error) {
      console.error("Error loading data:", error);
      // Fallback to mock data if live data fails
      if (useLive) {
        setStocks(fetchMockData());
        setUseLiveData(false);
      }
    }
    setIsLoading(false);
  };

  return (
    <div className="app">
      <h1>Stock Sorting Visualizer</h1>
      <Controls
        activeAlgorithm={activeAlgorithm}
        setActiveAlgorithm={setActiveAlgorithm}
        sortKey={sortKey}
        setSortKey={setSortKey}
        speed={speed}
        setSpeed={setSpeed}
        visualizationType={visualizationType}
        setVisualizationType={setVisualizationType}
        isSorting={isSorting}
        stocks={stocks}
        setStocks={setStocks}
         setIsSorting={setIsSorting}
         setMetrics={setMetrics}
        metrics={metrics}
      />
      <Visualization
        stocks={stocks}
        visualizationType={visualizationType}
        sortKey={sortKey}
      />
      
      <PerformanceMetrics 
         metrics={metrics}
         activeAlgorithm={activeAlgorithm}
         sortKey={sortKey}
         isSorting={isSorting}
      />
      <DataSourceToggle
        useLiveData={useLiveData}
        setUseLiveData={setUseLiveData}
        isSorting={false} // Pass your actual sorting state here
        onDataChange={handleDataChange}
      />
      
      {/* Your visualization component that handles stock selection */}
      <div onClick={() => handleStockSelect(someStock)}>
        {/* Visualization implementation */}
      </div>
      
      <StockInfoPanel 
        selectedStock={selectedStock} 
        stocks={stocks} 
      />


       </div>
  );
}

export default App;
