import React, { useEffect, useState } from 'react';
import { algorithmDescriptions } from '../../algorithms/algorithmHelper';

const Metrics = ({ stocks, activeAlgorithm, sortKey, isSorting, sortTime }) => {
    const [metrics, setMetrics] = useState({
        comparisons: 0,
        swaps: 0,
        startTime: null,
        timeTaken: 0
    });

    useEffect(() => {
        if (isSorting) {
            setMetrics(prev => ({
                ...prev,
                startTime: performance.now(),
                comparisons: 0,
                swaps: 0
            }));
        } else if (metrics.startTime) {
            setMetrics(prev => ({
                ...prev,
                timeTaken: performance.now() - prev.startTime
            }));
        }
    }, [isSorting]);

    const algorithmInfo = algorithmDescriptions[activeAlgorithm] || {};

    return (
        <div className="metrics">
            <h3>Performance Metrics</h3>
            <div className="metrics-grid">
                <div className="metric-card">
                    <h4>Algorithm</h4>
                    <p>{algorithmInfo.name}</p>
                </div>
                <div className="metric-card">
                    <h4>Time Complexity</h4>
                    <p>{algorithmInfo.complexity}</p>
                </div>
                <div className="metric-card">
                    <h4>Time Taken</h4>
                    <p>{metrics.timeTaken.toFixed(2)} ms</p>
                </div>
                <div className="metric-card">
                    <h4>Sort Key</h4>
                    <p>{sortKey}</p>
                </div>
                <div className="metric-card">
                    <h4>Comparisons</h4>
                    <p>{metrics.comparisons}</p>
                </div>
                <div className="metric-card">
                    <h4>Swaps</h4>
                    <p>{metrics.swaps}</p>
                </div>
            </div>
            
            <div className="algorithm-info">
                <h4>How {algorithmInfo.name} Works</h4>
                <p>{algorithmInfo.description}</p>
            </div>
        </div>
    );
};

export default Metrics;
