import React from 'react';
import './AlgorithmWinner.css';

const AlgorithmWinner = ({ algorithm1, algorithm2, metrics1, metrics2 }) => {
  if (!metrics1 || !metrics2) return null;

  const calculateScore = (metrics) => {
    // Lower is better for all metrics
    const timeScore = metrics.timeTaken;
    const comparisonScore = metrics.comparisons;
    const swapScore = metrics.swaps;

    return {
      time: timeScore,
      comparisons: comparisonScore,
      swaps: swapScore,
      total: timeScore * 0.4 + comparisonScore * 0.3 + swapScore * 0.3 // Weighted score
    };
  };

  const score1 = calculateScore(metrics1);
  const score2 = calculateScore(metrics2);

  const getWinner = () => {
    const score1Total = score1.total;
    const score2Total = score2.total;

    if (score1Total === score2Total) return "It's a tie!";
    return score1Total < score2Total ? algorithm1 : algorithm2;
  };

  const winner = getWinner();

  const getMetricWinner = (metric) => {
    if (score1[metric] === score2[metric]) return "tie";
    return score1[metric] < score2[metric] ? algorithm1 : algorithm2;
  };

  return (
    <div className="algorithm-winner">
      <h3 className="winner-title">Race Results</h3>

      <div className="winner-announcement">
        <h4>üèÜ Overall Winner: {winner}</h4>
        <p className="winner-explanation">Based on weighted performance across all metrics</p>
      </div>

      <div className="metrics-comparison">
        <div className="metric-row">
          <span className="metric-label">Time Taken:</span>
          <span className={`metric-winner ${getMetricWinner('time') === algorithm1 ? 'winner' : ''}`}>
            {algorithm1}: {metrics1.timeTaken.toFixed(2)}ms
          </span>
          <span className={`metric-winner ${getMetricWinner('time') === algorithm2 ? 'winner' : ''}`}>
            {algorithm2}: {metrics2.timeTaken.toFixed(2)}ms
          </span>
        </div>

        <div className="metric-row">
          <span className="metric-label">Comparisons:</span>
          <span className={`metric-winner ${getMetricWinner('comparisons') === algorithm1 ? 'winner' : ''}`}>
            {algorithm1}: {metrics1.comparisons}
          </span>
          <span className={`metric-winner ${getMetricWinner('comparisons') === algorithm2 ? 'winner' : ''}`}>
            {algorithm2}: {metrics2.comparisons}
          </span>
        </div>

        <div className="metric-row">
          <span className="metric-label">Swaps:</span>
          <span className={`metric-winner ${getMetricWinner('swaps') === algorithm1 ? 'winner' : ''}`}>
            {algorithm1}: {metrics1.swaps}
          </span>
          <span className={`metric-winner ${getMetricWinner('swaps') === algorithm2 ? 'winner' : ''}`}>
            {algorithm2}: {metrics2.swaps}
          </span>
        </div>
      </div>
    </div>
  );
};

export default AlgorithmWinner;
